import React, { useState } from "react";
import './App.css';
import data from './data.json';

function App() {
  const [selectedObject, setSelectedObject] = useState('');
  const [selectedStatus, setSelectedStatus] = useState('');
  const [result, setResult] = useState({ title: '', description: '' });
  const [rolledNumber, setRolledNumber] = useState(null);

  const handleObjectChange = (e) => {
    setSelectedObject(e.target.value);
    setSelectedStatus('');
    setResult({ title: '', description: '' });
    setRolledNumber(null);
  };

  const handleStatusChange = (e) => {
    setSelectedStatus(e.target.value);
    setResult({ title: '', description: '' });
    setRolledNumber(null);
  };

  const handleClick = () => {
    if (!selectedObject || !selectedStatus) {
      alert("Please select both an object and a status");
      return;
    }

    const randomNumber = Math.floor(Math.random() * 20) + 1;
    setRolledNumber(randomNumber); // Update the rolled number state

    const selectedItems = data[selectedObject][selectedStatus];
    const foundItem = selectedItems.find(item => item.numbers.includes(randomNumber));
    setResult(foundItem || { title: 'No result', description: 'No description available' });
  };

  const objectOptions = Object.keys(data).map(key => (
    <option key={key} value={key}>{key}</option>
  ));

  const statusOptions = selectedObject
    ? Object.keys(data[selectedObject]).map(key => (
        <option key={key} value={key}>{key}</option>
      ))
    : [];

  return (
    <div className="App">
      <header className="App-header">
        <div>
          <label>
            Select Object:
            <select value={selectedObject} onChange={handleObjectChange}>
              <option value="">--Choose an Object--</option>
              {objectOptions}
            </select>
          </label>
        </div>
        <div>
          <label>
            Select Status:
            <select value={selectedStatus} onChange={handleStatusChange} disabled={!selectedObject}>
              <option value="">--Choose a Status--</option>
              {statusOptions}
            </select>
          </label>
        </div>
        <button onClick={handleClick}>Generate Random Action</button>
        {rolledNumber !== null && <div><p>You rolled {rolledNumber}</p></div>}
        <div>
          <h3>Title: {result.title}</h3>
          <p>Description: {result.description}</p>
        </div>
      </header>
    </div>
  );
}

export default App;
